def skjkasdkd(lst):
    """Find the largest prime value in the list and return the sum of its digits."""
    largest_prime = 0
    for number in lst:
        if is_prime(number) and number > largest_prime:
            largest_prime = number
    return sum(int(digit) for digit in str(largest_prime))

