def count_nums(arr):
    """
    Write a function count_nums which takes an array of integers and returns
    the number of elements which has a sum of digits > 0.
    If a number is negative, then its first signed digit will be negative:
    e.g. -123 has signed digits -1, 2, and 3.
    >>> count_nums([]) == 0
    >>> count_nums([-1, 11, -11]) == 1
    >>> count_nums([1, 1, 2]) == 3
    """
    def sum_of_digits(num):
        if num < 0:
            return -int(str(num)[1]) + sum(int(digit) for digit in str(num)[2:])
        else:
            return sum(int(digit) for digit in str(num))

    return sum(1 for num in arr if sum_of_digits(num) > 0)

# Example usage:
print(count_nums([]))  # Output: 0
print(count_nums([-1, 11, -11]))  # Output: 1
print(count_nums([1, 1, 2]))  # Output: 3

def move_one_ball(arr):
    if not arr:
        return True

    # Find the index where the array is not sorted
    index = -1
    for i in range(len(arr) - 1):
        if arr[i] > arr[i + 1]:
            if index == -1:
                index = i
            else:
                # If there is more than one place where the array is not sorted,
                # it's not possible to sort it with right shift operations
                return False

    # If the array is already sorted or can be sorted by one series of right shifts
    if index == -1 or index == len(arr) - 2:
        return True

    # Check if the array can be sorted by shifting the elements from index + 1 to the beginning
    return arr[index + 1] <= arr[0] and all(arr[i] <= arr[i + 1] for i in range(index + 1, len(arr) - 1))

# Example usage:
print(move_one_ball([3, 4, 5, 1, 2]))  # True
print(move_one_ball([3, 5, 4, 1, 2]))  # False

from collections import Counter

def histogram(test):
    """Given a string representing a space separated lowercase letters, return a dictionary
    of the letter with the most repetition and containing the corresponding count.
    If several letters have the same occurrence, return all of them.

    Example:
    histogram('a b c') == {'a': 1, 'b': 1, 'c': 1}
    histogram('a b b a') == {'a': 2, 'b': 2}
    histogram('a b c a b') == {'a': 2, 'b': 2}
    histogram('b b b b a') == {'b': 4}
    histogram('') == {}

    """
    # Remove spaces and count occurrences of each letter
    counts = Counter(test.replace(" ", ""))

    # Find the maximum count
    max_count = max(counts.values(), default=0)

    # Return a dictionary with letters that have the maximum count
    return {letter: count for letter, count in counts.items() if count == max_count}

# Example usage:
print(histogram('a b c'))        # Output: {'a': 1, 'b': 1, 'c': 1}
print(histogram('a b b a'))      # Output: {'a': 2, 'b': 2}
print(histogram('a b c a b'))    # Output: {'a': 2, 'b': 2}
print(histogram('b b b b a'))    # Output: {'b': 4}
print(histogram(''))             # Output: {}

def tri(n):
    # Base cases
    if n == 0:
        return [1]
    elif n == 1:
        return [1, 3]
    elif n == 2:
        return [1, 3, 2]

    # Initialize the sequence with the base cases
    sequence = [1, 3, 2]

    # Compute the rest of the sequence
    for i in range(3, n + 1):
        if i % 2 == 0:
            # If i is even, use the formula 1 + i / 2
            sequence.append(1 + i // 2)
        else:
            # If i is odd, use the sum of the previous three terms
            sequence.append(sequence[-1] + sequence[-2] + sequence[-3])

    return sequence

# Example usage:
print(tri(3))  # Output should be [1, 3, 2, 8] based on the corrected recurrence relation

